"use strict";
Object.defineProperty(exports, "__esModule", { value: true });
exports.formatNumber = void 0;
exports.formatNumber = (value) => {
    if (!value)
        return '';
    const [intNumber, decimals] = value.replace(/,/g, '').split('.');
    const regex = /^[0-9]*$/;
    let formattedNumber = '';
    if (intNumber.match(regex)) {
        for (let i = 0; i < intNumber.length; i++) {
            formattedNumber += intNumber[i];
            // add a comma on every 3rd digit
            if ((intNumber.length - i) % 3 === 1) {
                formattedNumber += ',';
            }
        }
        // remove the , at the end
        formattedNumber = formattedNumber.slice(0, -1);
        // if decimal points exist add them
        if (decimals !== undefined) {
            formattedNumber += `.${decimals}`;
        }
    }
    return formattedNumber;
};
