import React, { useState } from "react";
import Modal from "react-modal";
import {
  Row,
  Column,
  Container,
  Component,
  CompanySelect,
  Companies,
  EmailClient,
  ClientLogo
} from "./Components";
import "./Signature.css";
import { Text, PrimaryButton, Input } from "@fairfx/geometry-web";

export default function Instructions() {
  const [outlookWeb, openOutlookWeb] = useState(false);
  const [outlookDesktop, openOutlookDesktop] = useState(false);
  const [appleMail, openAppleMail] = useState(false);

  function showOutlook() {
    openOutlookWeb(!outlookWeb);
  }

  function showOutlookDesktop() {
    openOutlookDesktop(!outlookDesktop);
  }

  function showAppleMail() {
    openAppleMail(!appleMail);
  }

  return (
    <>


      <Container>
        <Text as="h4">Your email signature:</Text>

      <CompanySelect>
        <Companies>
          <EmailClient onClick={showOutlook}><ClientLogo src="https://download.logo.wine/logo/Microsoft_Outlook/Microsoft_Outlook-Logo.wine.png" />Outlook Web</EmailClient>
          <EmailClient onClick={showOutlookDesktop}><ClientLogo src="https://www.freepnglogos.com/uploads/logo-outlook/transparent-outlook-icon-2.png" />Outlook Desktop</EmailClient>
          <EmailClient onClick={showAppleMail}><ClientLogo src="https://upload.wikimedia.org/wikipedia/commons/thumb/4/4e/Mail_%28iOS%29.svg/1200px-Mail_%28iOS%29.svg.png" />Apple Mail</EmailClient>
        </Companies>
        </CompanySelect>

        <Component signature={false}>
          <Row>
      <Modal
        isOpen={outlookWeb}
        onRequestClose={showOutlook}
        contentLabel="My dialog"
      >
        <div>outlook web</div>
        <button onClick={showOutlook}>Close modal</button>
      </Modal>

      <Modal
        isOpen={outlookDesktop}
        onRequestClose={showOutlookDesktop}
        contentLabel="My dialog"
      >
        <div>outlook desktop</div>
        <button onClick={showOutlookDesktop}>Close modal</button>
      </Modal>

      <Modal
        isOpen={appleMail}
        onRequestClose={showAppleMail}
        contentLabel="My dialog"
      >
        <div>apple mail</div>
        <button onClick={showAppleMail}>Close modal</button>
            </Modal>

          </Row>
        </Component>
        </Container>
    </>
  );
}
