import React from 'react';
import styled from 'styled-components';

const Logo = "https://cdn.fairfx.com/images/group-email-signature/equals-dark.png"

const Background = styled.div`
  background-color: black;
  position: relative;
  z-index: 1;
`;

const Wrapper = styled.div`
  height: 4rem;
`;

const LogoWrapper = styled.div`
  display: flex;
  margin-right: 0;

  @media (min-width: 1200px) {
    display: flex;
    height: 4rem;
  }
`;

const LogoLink = styled.a`
  height: 1.625rem;
  display: flex;
  align-items: center;
  align-self: center;
`;

const SecondaryNav = styled.div`
  display: flex;
  padding: 0;
  list-style: none;
  text-transform: uppercase;
  font-size: 0.6875rem;
  font-weight: 700;
  color: red!important;
  margin: 0 -0.75rem;
  align-items: center;
  justify-content: flex-end;
  margin-left: auto;
`;

const SecondaryNavItem = styled.a`
  cursor: pointer;
  position: relative;
  color: red!important;
  padding: 0 0.75rem;
  transition: all 0.3s;
  text-decoration: none;

  &::after {
    content: '';
    position: absolute;
    bottom: -0.25rem;
    left: 50%;
    right: 50%;
    height: 0.0625rem;
    background-color: white!important;
    transition: all 0.3s;
  }

  :hover {
    color: red!important;
  }
`;

const HeaderNav = () => {
  return (
    <Background>
      <Wrapper>
        <LogoWrapper>
          <img src={Logo} />
          <SecondaryNavItem>View our Terms â†’</SecondaryNavItem>
        </LogoWrapper>
      </Wrapper>
    </Background>
  );
};

export default HeaderNav;
