import React, { useState } from "react";
import Modal from "react-modal";

export default function Instructions() {
  const [outlookWeb, openOutlookWeb] = useState(false);
  const [outlookDesktop, openOutlookDesktop] = useState(false);
  const [appleMail, openAppleMail] = useState(false);

  function showOutlook() {
    openOutlookWeb(!outlookWeb);
  }

  function showOutlookDesktop() {
    openOutlookDesktop(!outlookDesktop);
  }

  function showAppleMail() {
    openAppleMail(!appleMail);
  }

  return (
    <div className="App">
      <button onClick={showOutlook}>Open modal</button>
      <button onClick={showOutlookDesktop}>Open modal</button>
      <button onClick={showAppleMail}>Open modal</button>

      <Modal
        isOpen={outlookWeb}
        onRequestClose={showOutlook}
        contentLabel="My dialog"
      >
        <div>outlook web</div>
        <button onClick={showOutlook}>Close modal</button>
      </Modal>

      <Modal
        isOpen={outlookDesktop}
        onRequestClose={showOutlookDesktop}
        contentLabel="My dialog"
      >
        <div>outlook desktop</div>
        <button onClick={showOutlookDesktop}>Close modal</button>
      </Modal>

      <Modal
        isOpen={appleMail}
        onRequestClose={showAppleMail}
        contentLabel="My dialog"
      >
        <div>apple mail</div>
        <button onClick={showAppleMail}>Close modal</button>
      </Modal>
    </div>
  );
}
