import React, { useState } from "react";
import Modal from "react-modal";
import {
  Row,
  CompanySelect,
  Companies,
  EmailClient,
  ClientLogo,
  CloseModal,
  modalPopup,
} from "./Components";
import "./Signature.css";
import { Text } from "@fairfx/geometry-web";

export default function Instructions() {
  const [outlookWeb, openOutlookWeb] = useState(false);
  const [outlookDesktop, openOutlookDesktop] = useState(false);
  const [appleMail, openAppleMail] = useState(false);

  function showOutlook() {
    openOutlookWeb(!outlookWeb);
  }

  function showOutlookDesktop() {
    openOutlookDesktop(!outlookDesktop);
  }

  function showAppleMail() {
    openAppleMail(!appleMail);
  }

  return (
    <>
      <Text fontSize="large" fontWeight="bold">
        Instructions:
      </Text>

      <CompanySelect>
        <Companies>
          <EmailClient onClick={showOutlook}>
            <ClientLogo src="https://download.logo.wine/logo/Microsoft_Outlook/Microsoft_Outlook-Logo.wine.png" />
            Outlook Web
          </EmailClient>
          <EmailClient onClick={showOutlookDesktop}>
            <ClientLogo src="https://www.freepnglogos.com/uploads/logo-outlook/transparent-outlook-icon-2.png" />
            Outlook Desktop
          </EmailClient>
          <EmailClient onClick={showAppleMail}>
            <ClientLogo src="https://upload.wikimedia.org/wikipedia/commons/thumb/4/4e/Mail_%28iOS%29.svg/1200px-Mail_%28iOS%29.svg.png" />
            Apple Mail
          </EmailClient>
        </Companies>
      </CompanySelect>

      <Row>
        <Modal
          isOpen={outlookWeb}
          onRequestClose={showOutlook}
          style={modalPopup}
       >
          <CloseModal onClick={showOutlook}>✖</CloseModal>
          <Text as="h3">Instructions for Outlook Web</Text>
          <h4>Instructions for Outlook Web</h4>{" "}
          <p>
            1. At the top of the Outlook Web App page on the right hand side{" "}
          </p>
          <ol>
            <li>Click Settings</li>
            <li>Scroll down to Mail and press click</li>
            <li>
              Once the Mail Options tab is open scroll down and find Layout
            </li>
            <li>In layout, click email signatures</li>
          </ol>
          <p>
            2. Copy and paste the email signature you have created from the
            FairFX Email Signature Tool into the “Email Signature” box.{" "}
          </p>
          <p>
            3. To add your signature to all outgoing messages, select the
            “Automatically include my signature on messages I send” checkbox.
          </p>
          <ul>
            <li>
              If you use both Outlook Web App and Outlook and want a signature
              in both, you need to create a signature in each.
            </li>
          </ul>
          <p>4. Click Save.</p>
        </Modal>

        <Modal isOpen={outlookDesktop} onRequestClose={showOutlookDesktop}
          style={modalPopup}
       >
          <CloseModal onClick={showOutlook}>✖</CloseModal>
          <Text as="h3">Instructions for Outlook Desktop</Text>


          <h4>Instructions for Outlook Desktop</h4> <p>1. Open a new email message.2. Select Signature : Signatures from the Message menu.3. Depending on the size of your Outlook window and whether you're composing a new email message or a reply or forward, the Message menu and the Signature button might be in two different locations.4. Under Select signature to edit, choose New, and in the New Signature dialog box, type a name for the signature.5. Under Edit signature, compose your signature. 6. Then copy and paste the signature into the Edit signature box from the FairFX Email Signature Tool. 7. If you want your signature added to all new messages by default, in the New Messages drop-down box, select one of your signatures. If you don't want to automatically add a signature to new messages, choose (none). This doesn't add a signature to any messages you reply to or forward.8. If you want your signature to appear in the messages you reply to and forward, in the Replies/forwards drop-down, select one of your signatures. Otherwise, accept the default option of (none). 9. Choose OK to save your new signature and return to your message. 10. Outlook doesn't add your new signature to the message you opened in Step 1, even if you chose to apply the signature to all new messages. You'll have to add the signature manually to this one message. All future messages will have the signature added automatically. To add the signature manually, select Signature from the Message menu and then pick the signature you just created. </p>



        </Modal>

        <Modal isOpen={appleMail} onRequestClose={showAppleMail}
          style={modalPopup}
       >
          <CloseModal onClick={showAppleMail}>✖</CloseModal>
              <Text as="h3">Instructions for Apple Mail</Text>


        </Modal>
      </Row>
    </>
  );
}
