#!/usr/bin/env bash

set -eu

if [ ! -d ./node_modules/react-native ]; then
  echo 'This script needs to be run from the root of a React Native project!'
  exit 1
fi

cp ./node_modules/react-native/template/_gitignore ./.gitignore
printf "\n%s\n" "$(cat ./node_modules/@fairfx/wc-configs/mobile/gitignore/gitignore)" >> ./.gitignore
