const helpers = require('../../src/helpers');
const isMonorepo = helpers.isMonorepo;
const rootPath = helpers.rootPath;

const packagesPath = isMonorepo()
  ? `${rootPath}/packages`
  : `${rootPath}/node_modules/@fairfx`;
const lintStagedConfigPath = `${packagesPath}/wc-configs/shared/husky/lint-staged.config.js`;

module.exports = {
  hooks: {
    'pre-commit': `lint-staged --config ${lintStagedConfigPath}`,
    'commit-msg': 'commitlint -E HUSKY_GIT_PARAMS',
  },
};
