#!/usr/bin/env bash

set -eu

cd "$(dirname "$0")"/..

if [ ! -d babel ]; then
  ln -s web/babel babel
fi

if [ ! -d commitizen ]; then
  ln -s shared/commitizen commitizen
fi

if [ ! -d commitlint ]; then
  ln -s shared/commitlint commitlint
fi

if [ ! -d husky ]; then
  ln -s shared/husky husky
fi

if [ ! -d jest ]; then
  ln -s web/jest jest
fi

if [ ! -d prettier ]; then
  ln -s shared/prettier prettier
fi

if [ ! -d rollup ]; then
  ln -s web/rollup rollup
fi

if [ ! -d stylelint ]; then
  ln -s web/stylelint stylelint
fi

if [ ! -d tsconfig ]; then
  ln -s web/tsconfig tsconfig
fi

if [ ! -d tslint ]; then
  ln -s web/tslint tslint
fi
